mapper.select:
  SELECT t1.facility_id facilityId,
         t1.task_id taskId,
         t1.entrust_id entrustId,
	     (select t2.name from tb_enforce_entrust t2 where t2.id = t1.entrust_id) entrustName,
		 t1.cid cid,
		 t3.factory_no factoryNo,
		 t3.facility_name facilityName,
		 t3.facility_type facilityType,
		 t3.model model,
		 t3.accuracy accuracy,
		 t3.test_range testRange,
		 t1.expect_price expectPrice,
		 t1.test_result testResult
	FROM tb_enforce_entrust_facility t1, tb_facility t3

         t1.facility_id = t3.id
     AND t1.entrust_id = #{entrustId}



				and t3.note ${query_expr} '%${note}%'


				and t3.factory_no ${query_expr} '%${factoryNo}%'


				and t3.factory_type ${query_expr} '%${factoryType}%'


				and t3.facility_name ${query_expr} '%${facilityName}%'


				and t3.model ${query_expr} '%${model}%'


				and t1.test_result ${query_expr} '%${testResult}%'




				and t3.note ${query_expr} #{note}


				and t3.factory_no ${query_expr} #{factoryNo}


				and t3.factory_type ${query_expr} #{factoryType}


				and t3.facility_name ${query_expr} #{facilityName}


				and t3.model ${query_expr} #{model}


				and t1.test_result ${query_expr} #{testResult}




				and t3.last_test_date >= #{startDate}


				and t3.last_test_date <= #{endDate}

  SELECT t1.id id,
  		 t1.note text
	FROM tb_inquiry t1
   WHERE t1.company_id = #{cid}
     AND t1.status = #{status}
